body.script:
document.getElementById("subscribeForm").addEventListener("submit", function (e) {
  e.preventDefault();

  const form = e.target;
  const email = form.email.value;
  const responseEl = document.getElementById("formResponse");

  fetch("https://script.google.com/macros/s/AKfycbzMriEPBCXELHfjsfTc8NLTA0MjcVmoZrfvpspizQ1Y4KvklopIzPfsIs95fVjpmXeXEQ/exec", {
    method: "POST",
    body: new URLSearchParams({ email }),
    headers: { "Content-Type": "application/x-www-form-urlencoded" }
  })
  .then(res => res.text())
  .then(text => {
    responseEl.style.display = "block";
    if (text === "Already Subscribed") {
      responseEl.style.color = "orange";
      responseEl.innerText = "You're already subscribed!";
    } else if (text === "Subscribed Successfully") {
      responseEl.style.color = "green";
      responseEl.innerText = "Thank you for subscribing!";
      form.reset();
    } else {
      responseEl.style.color = "red";
      responseEl.innerText = "Oops! you'r already listed.";
    }
  })
  .catch(err => {
    responseEl.style.color = "red";
    responseEl.innerText = "Submission failed. Please try again.";
    responseEl.style.display = "block";
  });
});

