(function () {
  var sections = [
    { id: 'custom-grid-posts', label: 'food business tips' },
    { id: 'recipes-grid', label: 'recipes' },
    { id: 'kitchen-grid', label: 'kitchen tips' }
  ];

  function loadGridPosts(label, containerId) {
    var url = "/feeds/posts/default/-/" + encodeURIComponent(label) + "?alt=json&max-results=4";

    fetch(url)
      .then(response => response.json())
      .then(data => {
        var entries = data.feed.entry || [];
        var container = document.getElementById(containerId);
        if (!container) return;

        var html = "";

        entries.forEach(entry => {
          var title = entry.title.$t;
          var link = entry.link.find(l => l.rel === "alternate")?.href || "#";

          var thumbnail = "https://via.placeholder.com/320x200?text=No+Image";
          if (entry.media$thumbnail) {
            thumbnail = entry.media$thumbnail.url.replace(/\/s\d+(-c)?/, "/s400");
          } else if (entry.content && entry.content.$t) {
            var match = entry.content.$t.match(/<img[^>]+src="([^"]+)"/i);
            if (match && match[1]) {
              thumbnail = match[1].replace(/\/s\d+(-c)?/, "/s400");
            }
          }

          html += '<div class="grid-item">';
          html += '<a href="' + link + '" title="' + title + '">';
          html += '<img src="' + thumbnail + '" alt="' + title + '">';
          html += '<h4>' + title + '</h4>';
          html += '</a>';
          html += '<div class="blogger-share-icon"><i class="fas fa-share-alt"></i></div>';
          html += '</div>';
        });

        container.innerHTML = html;
      })
      .catch(error => {
        console.error("Error loading posts for label " + label + ":", error);
      });
  }

  // 🔄 Load all sections immediately on page load
  document.addEventListener("DOMContentLoaded", function () {
    sections.forEach(section => {
      loadGridPosts(section.label, section.id);
    });
  });
})();
//
